package com.n2s.lab_exercise;

import java.util.Arrays;

public class SortCharArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char[] arr = { 'j', 'a', 'n' };
		System.out.println(Arrays.toString(arr));
		Arrays.sort(arr);
		System.out.println(Arrays.toString(arr));

	}

}
